from sklearn.feature_extraction.text import CountVectorizer
from sklearn.metrics.pairwise import cosine_similarity
import pandas as pd
# model set to go

def history(usr):
  recipe_book = pd.read_excel('recipe_dataset.xlsx')
  user_info = pd.read_excel('user_info.xlsx')
  dishes_index = [int(user_info[user_info['user'] == usr].iloc[:, i].values) for i in range(4, 9)]
  dishes = [recipe_book.iloc[i,:] for i in dishes_index]
  return dishes


def recommendation(final_dataset,X):
  final_dataset.set_index('TranslatedRecipeName', inplace=True)
  count_vec = CountVectorizer()
  count_matrix = count_vec.fit_transform(final_dataset.TranslatedIngredients)
  count_x = count_vec.transform(X)
  cosine_sim = cosine_similarity(count_x,count_matrix)
  print(cosine_sim)
  score_series = pd.Series(cosine_sim[0]).sort_values(ascending= False)
  top_5 = list(score_series.iloc[:4].index)
  for i in top_5:
    print(final_dataset.index[i])

